746e39ffdea3617f152c8a0801ecc3e518d749e7.source:
query State {
  viewer {
    __typename
    id
    login
    email
    avatarUrl(size: 120)
    pullRequests(first: 100, states: OPEN) {
      __typename
      totalCount
      nodes {
        __typename
        id
        url
        title
        baseRepository {
          __typename
          name
        }
        author {
          __typename
          login
          avatarUrl(size: 120)
        }
        createdAt
        number
        state
        comments {
          __typename
          totalCount
        }
        mergeable
        reviewDecision
        reviewRequests(first: 10) {
          __typename
          totalCount
          nodes {
            __typename
            requestedReviewer {
              __typename
              ... on User {
                __typename
                id
                login
                email
                avatarUrl
              }
            }
          }
        }
        reviews(first: 10) {
          __typename
          totalCount
          nodes {
            __typename
            state
            author {
              __typename
              login
              avatarUrl
            }
            comments(first: 10) {
              __typename
              nodes {
                __typename
                author {
                  __typename
                  login
                  avatarUrl
                }
              }
            }
          }
        }
      }
    }
  }
}
